SELECT * FROM ( SELECT A.SUB_TASK_NO , A.SEND_DT , A.END_DT , A.WORK_STATUS , B.TASK_NO , B.TASK_NM , B.CHANNEL , B.SEND_REPEAT , B.STATUS , B.SEG_NO , B.SEND_TERM_END_DT , B.SEND_TERM_LOOP , B.REG_DT , B.CONT_FL_PATH , B.SURVEYNO , C.SEG_NM , D.USER_NM , E.CAMP_NM , F.CD_NM CHANNEL_NM , G.CD_NM SEND_REPEAT_NM , H.CD_NM STATUS_NM , ROW_NUMBER() OVER (ORDER BY A.SEND_DT DESC, A.TASK_NO DESC, A.SUB_TASK_NO DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_SUBTASK A , NEO_TASK B , NEO_SEGMENT C , NEO_USER D , NEO_CAMPAIGN E , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C002' AND UILANG = '000') F , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C017' AND UILANG = '000') G , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C101' AND UILANG = '000') H
WHERE A.SEND_TEST_YN != 'Y' AND A.WORK_STATUS IN ('002','003') AND A.TASK_NO = B.TASK_NO AND B.SEG_NO = C.SEG_NO AND B.USER_ID = D.USER_ID AND B.CAMP_NO = E.CAMP_NO AND B.CHANNEL = F.CD AND B.SEND_REPEAT = G.CD AND A.WORK_STATUS = H.CD AND B.SEND_REPEAT = '000'
AND UPPER(B.TASK_NM) LIKE '%' || #{searchTaskNm} || '%'
AND B.DEPT_NO = #{searchDeptNo}
AND B.DEPT_NO = #{searchDeptNo}
AND B.USER_ID = #{searchUserId}
AND B.CAMP_NO = #{searchCampNo}
AND B.SEG_NO = #{searchSegNo}
AND A.SEND_DT >= #{searchStartDt} || '000000'
AND A.SEND_DT < #{searchEndDt} ||'240000'
AND B.TASK_NO = #{taskNo}
) WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT SEND_CNT
	 , SUCC_CNT
	 , (SEND_CNT - SUCC_CNT) AS FAIL_CNT
	 , OPEN_CNT
	 , VALID_CNT
	 , CLICK_CNT
	 , BLOCK_CNT
FROM	(
			SELECT NVL(SUM(SEND_AR_AMT),0) SEND_CNT
				 , NVL(SUM(CASE WHEN SEND_RCODE = '000' THEN SEND_AR_AMT ELSE 0 END),0) SUCC_CNT
			  FROM NEO_AR_SENDLOG
			 WHERE TASK_NO = #{taskNo}
			   AND SUB_TASK_NO = #{subTaskNo}
		) A,
		(
			SELECT NVL(SUM(RESP_AR_AMT),0) OPEN_CNT
				 , NVL(SUM(CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT ELSE 0 END),0) BLOCK_CNT
			  FROM NEO_AR_RESPLOG
			 WHERE TASK_NO = #{taskNo}
			   AND SUB_TASK_NO = #{subTaskNo}
		) B,
		(
			SELECT NVL(SUM(CLICK_AR_AMT),0) CLICK_CNT
				 , NVL(SUM(VALID_AR_AMT),0) VALID_CNT
			  FROM NEO_AR_LINKLOG
			 WHERE TASK_NO = #{taskNo}
			   AND SUB_TASK_NO = #{subTaskNo}
		) D

SELECT *
  FROM (
		SELECT A.CUST_ID
			 , A.CUST_EM
			 , A.CUST_NM
			 , A.RETRY_CNT
			 , A.SEND_DT
			 , A.SEND_MSG
			 , ROW_NUMBER() OVER (ORDER BY A.CUST_ID, A.CUST_EM, A.CUST_NM) SEQ
			 , COUNT(1) OVER() TOTAL_COUNT
		  FROM (
			  		SELECT SUB_TASK_NO, CUST_ID, CUST_EM, CUST_NM, RETRY_CNT, SEND_DT, SEND_MSG
					  FROM NEO_SENDLOG
					 WHERE TASK_NO = #{taskNo}
					   AND SEND_RCODE != '000'

					   AND SUB_TASK_NO = #{subTaskNo}


					   AND RCODE_STEP1 = #{step1}


					   AND RCODE_STEP2 = #{step2}


					   AND RCODE_STEP3 = #{step3}


					   AND CUST_DOMAIN = #{domain}

				) A
				JOIN
				(
					SELECT SUB_TASK_NO, CUST_ID, CUST_EM, CUST_NM, MAX(RETRY_CNT) AS MAX_RETRY_CNT
					  FROM NEO_SENDLOG
					 WHERE TASK_NO = #{taskNo}
					   AND SEND_RCODE != '000'

					   AND SUB_TASK_NO = #{subTaskNo}


					   AND RCODE_STEP1 = #{step1}


					   AND RCODE_STEP2 = #{step2}


					   AND RCODE_STEP3 = #{step3}


					   AND CUST_DOMAIN = #{domain}

					 GROUP BY SUB_TASK_NO, CUST_ID, CUST_EM, CUST_NM
				) B
			 ON A.CUST_ID = B.CUST_ID
			AND A.CUST_EM = B.CUST_EM
			AND A.CUST_NM = B.CUST_NM
			AND A.RETRY_CNT = B.MAX_RETRY_CNT
		)
 WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT *
  FROM (
		SELECT TO_CHAR(TO_DATE(A.SEND_DT,'YYYYMMDDHH24MISS'), 'YYYYMMDDHH24') AS SEND_TIME
			 , NVL(SUM(SEND_AR_AMT), 0) AS SEND_CNT
			 , NVL(SUM((CASE WHEN A.SEND_RCODE = '000' THEN A.SEND_AR_AMT END)), 0) AS SUCC_CNT
			 , ROW_NUMBER() OVER (ORDER BY TO_CHAR(TO_DATE(A.SEND_DT,'YYYYMMDDHH24MISS'), 'YYYYMMDDHH24')) SEQ
			 , COUNT(1) OVER () TOTAL_COUNT
		  FROM NEO_AR_SENDLOG A
		 WHERE A.TASK_NO = #{taskNo}
		   AND A.SUB_TASK_NO = #{subTaskNo}
		 GROUP BY TO_CHAR(TO_DATE(A.SEND_DT,'YYYYMMDDHH24MISS'), 'YYYYMMDDHH24')
		)
 WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT CASE WHEN SUM(SEND_AR_AMT) IS NULL THEN 0 ELSE SUM(SEND_AR_AMT) END AS SEND_CNT
	 , CASE WHEN SUM((CASE WHEN A.SEND_RCODE = '000' THEN A.SEND_AR_AMT END)) IS NULL THEN 0 ELSE SUM((CASE WHEN A.SEND_RCODE = '000' THEN A.SEND_AR_AMT END)) END AS SUCC_CNT
  FROM NEO_AR_SENDLOG A
 WHERE A.TASK_NO = #{taskNo}
   AND A.SUB_TASK_NO = #{subTaskNo}

SELECT SUCC_CNT, OPEN_CNT, CLICK_CNT, VALID_CNT, BLOCK_CNT
  FROM (
		SELECT SUM(SEND_AR_AMT) AS SUCC_CNT FROM NEO_AR_SENDLOG
		 WHERE TASK_NO = #{taskNo}
		   AND SUB_TASK_NO = #{subTaskNo}
		   AND SEND_RCODE = '000'
		) X,
		(
		SELECT SUM(OPEN_CNT) AS OPEN_CNT, SUM(CLICK_CNT) AS CLICK_CNT, SUM(VALID_CNT) AS VALID_CNT, SUM(BLOCK_CNT) AS BLOCK_CNT
		  FROM (
				SELECT SUM(AA.RESP_AR_AMT) AS OPEN_CNT, 0 AS CLICK_CNT, 0 AS VALID_CNT
					, SUM(CASE WHEN AA.BLOCKED_YN = 'Y' THEN AA.RESP_AR_AMT ELSE 0 END) AS BLOCK_CNT
				  FROM NEO_AR_RESPLOG AA
				 WHERE AA.TASK_NO = #{taskNo}
				   AND AA.SUB_TASK_NO = #{subTaskNo}
				 UNION ALL
				SELECT 0 AS OPEN_CNT, SUM(AA.CLICK_AR_AMT) AS CLICK_CNT, SUM(AA.VALID_AR_AMT) AS VALID_CNT, 0 AS BLOCK_CNT
				  FROM NEO_AR_LINKLOG AA
				 WHERE AA.TASK_NO = #{taskNo}
				   AND AA.SUB_TASK_NO = #{subTaskNo}
				) YY
		 ) Y

