AND A.SEG_NO = #{searchSegNo}
) P , NEO_SUBTASK Q , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C101' AND UILANG = #{uilang}) R WHERE P.TASK_NO = Q.TASK_NO AND P.SUB_TASK_NO = Q.SUB_TASK_NO AND Q.WORK_STATUS NOT IN ('000', '001', '002', '201', '202', '203', '204') ANd Q.SEND_TEST_YN ='N' AND Q.WORK_STATUS = R.CD ) WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

