SELECT OPEN_CNT , BLOCK_CNT , VALID_CNT , CLICK_CNT FROM ( SELECT COALESCE(SUM((CASE WHEN BLOCKED_YN = 'N' THEN RESP_AR_AMT END)),0) AS OPEN_CNT , COALESCE(SUM((CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT END)),0) AS BLOCK_CNT FROM NEO_AR_RESPLOG WHERE
( NEO_AR_RESPLOG.TASK_NO = #{joinKey.taskNo} AND NEO_AR_RESPLOG.SUB_TASK_NO = #{joinKey.subTaskNo} )
) A, ( SELECT COALESCE(SUM(CLICK_AR_AMT),0) AS CLICK_CNT, COALESCE(SUM(VALID_AR_AMT),0) AS VALID_CNT FROM NEO_AR_LINKLOG WHERE
( NEO_AR_LINKLOG.TASK_NO = #{joinKey.taskNo} AND NEO_AR_LINKLOG.SUB_TASK_NO = #{joinKey.subTaskNo} )
) C

SELECT * FROM ( SELECT P.TASK_NO , Q.SUB_TASK_NO , Q.SEND_DT , Q.WORK_STATUS , R.CD_NM WORK_STATUS_NM , P.TASK_NM , P.CHANNEL , P.SEND_REPEAT , P.STATUS , P.SEG_NO , P.SEND_TERM_END_DT , P.SEND_TERM_LOOP , P.REG_DT , P.CONT_FL_PATH , P.SURVEYNO , P.SEG_NM , P.USER_NM , P.CAMP_NM , P.CHANNEL_NM , P.SEND_REPEAT_NM , P.STATUS_NM , ROW_NUMBER() OVER (ORDER BY Q.SEND_DT DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM ( SELECT A.TASK_NO , A.TASK_NM , A.CHANNEL , A.SEND_REPEAT , A.STATUS , A.SEG_NO , A.SEND_TERM_END_DT , A.SEND_TERM_LOOP , A.REG_DT , A.CONT_FL_PATH , A.SURVEYNO , C.SEG_NM , D.USER_NM , E.CAMP_NM , F.CD_NM CHANNEL_NM , G.CD_NM SEND_REPEAT_NM , H.CD_NM STATUS_NM , (SELECT MAX(SUB_TASK_NO) SUB_TASK_NO FROM NEO_SUBTASK WHERE TASK_NO = A.TASK_NO AND WORK_STATUS NOT IN ('000', '001', '002', '201', '202', '203', '204')
AND SEND_DT >= #{searchStartDt} || '000000'
AND SEND_DT < #{searchEndDt} ||'240000'
) SUB_TASK_NO FROM NEO_TASK A , NEO_SEGMENT C , NEO_USER D , NEO_CAMPAIGN E , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C002' AND UILANG = #{uilang}) F , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C017' AND UILANG = #{uilang}) G , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C023' AND UILANG = #{uilang}) H WHERE A.SEG_NO = C.SEG_NO AND A.USER_ID = D.USER_ID AND A.CAMP_NO = E.CAMP_NO AND A.CHANNEL = F.CD AND A.SEND_REPEAT = G.CD AND A.STATUS = H.CD
AND A.SEND_REPEAT = #{searchSendRepeat}
AND UPPER(A.TASK_NM) LIKE '%' || #{searchTaskNm} || '%'
AND A.DEPT_NO = #{searchDeptNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
AND A.CAMP_NO = #{searchCampNo}
AND A.SEG_NO = #{searchSegNo}
) P , NEO_SUBTASK Q , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C101' AND UILANG = #{uilang}) R WHERE P.TASK_NO = Q.TASK_NO AND P.SUB_TASK_NO = Q.SUB_TASK_NO AND Q.WORK_STATUS NOT IN ('000', '001', '002', '201', '202', '203', '204') ANd Q.SEND_TEST_YN ='N' AND Q.WORK_STATUS = R.CD ) WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT * FROM ( SELECT A.SUB_TASK_NO , A.SEND_DT , A.END_DT , A.WORK_STATUS , B.TASK_NO , B.TASK_NM , B.CHANNEL , B.SEND_REPEAT , B.STATUS , B.SEG_NO , B.SEND_TERM_END_DT , B.SEND_TERM_LOOP , B.REG_DT , B.CONT_FL_PATH , B.SURVEYNO , C.SEG_NM , D.USER_NM , E.CAMP_NM , F.CD_NM CHANNEL_NM , G.CD_NM SEND_REPEAT_NM , H.CD_NM STATUS_NM , ROW_NUMBER() OVER (ORDER BY A.SEND_DT DESC, A.TASK_NO DESC, A.SUB_TASK_NO DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_SUBTASK A , NEO_TASK B , NEO_SEGMENT C , NEO_USER D , NEO_CAMPAIGN E , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C002' AND UILANG = '000') F , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C017' AND UILANG = '000') G , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C101' AND UILANG = '000') H
WHERE A.SEND_TEST_YN != 'Y' AND A.WORK_STATUS IN ('002','003') AND A.TASK_NO = B.TASK_NO AND B.SEG_NO = C.SEG_NO AND B.USER_ID = D.USER_ID AND B.CAMP_NO = E.CAMP_NO AND B.CHANNEL = F.CD AND B.SEND_REPEAT = G.CD AND A.WORK_STATUS = H.CD AND B.SEND_REPEAT = '001'
AND UPPER(B.TASK_NM) LIKE '%' || #{searchTaskNm} || '%'
AND B.DEPT_NO = #{searchDeptNo}
AND B.DEPT_NO = #{searchDeptNo}
AND B.USER_ID = #{searchUserId}
AND B.CAMP_NO = #{searchCampNo}
AND A.SEND_DT >= #{searchStartDt} || '000000'
AND A.SEND_DT < #{searchEndDt} ||'240000'
AND B.TASK_NO = #{taskNo}
) WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT NEO_AR_SENDLOG.TASK_NO , NEO_TASK.TASK_NM , (SELECT CD_NM FROM NEO_CD WHERE CD_GRP = 'C017' AND UILANG = '000' AND CD = NEO_TASK.SEND_REPEAT) SEND_REPEAT_NM , NEO_SEGMENT.SEG_NM , NEO_CAMPAIGN.CAMP_NM , COALESCE(SUM(SEND_AR_AMT),0) AS TOT_CNT , COALESCE(SUM((CASE WHEN NEO_AR_SENDLOG.SEND_RCODE = '000' THEN SEND_AR_AMT END)),0) AS SUC_CNT FROM NEO_AR_SENDLOG , NEO_TASK , NEO_SUBTASK , NEO_SEGMENT , NEO_CAMPAIGN WHERE NEO_AR_SENDLOG.TASK_NO = NEO_TASK.TASK_NO AND NEO_AR_SENDLOG.TASK_NO = NEO_SUBTASK.TASK_NO AND NEO_AR_SENDLOG.SUB_TASK_NO = NEO_SUBTASK.SUB_TASK_NO AND NEO_TASK.SEG_NO = NEO_SEGMENT.SEG_NO AND NEO_TASK.CAMP_NO = NEO_CAMPAIGN.CAMP_NO AND NEO_SUBTASK.WORK_STATUS NOT IN ('000','001','002','201','202','203','204' ) AND
NEO_AR_SENDLOG.TASK_NO = #{joinKey.taskNo}
GROUP BY NEO_AR_SENDLOG.TASK_NO, NEO_TASK.TASK_NM, NEO_SEGMENT.SEG_NM, NEO_CAMPAIGN.CAMP_NM, NEO_TASK.SEND_REPEAT ORDER BY NEO_AR_SENDLOG.TASK_NO DESC

SELECT OPEN_CNT , BLOCK_CNT , VALID_CNT , CLICK_CNT FROM ( SELECT COALESCE(SUM((CASE WHEN BLOCKED_YN = 'N' THEN RESP_AR_AMT END)),0) AS OPEN_CNT, COALESCE(SUM((CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT END)),0) AS BLOCK_CNT FROM NEO_AR_RESPLOG WHERE
NEO_AR_RESPLOG.TASK_NO = #{joinKey.taskNo}
) A, ( SELECT COALESCE(SUM(CLICK_AR_AMT),0) AS CLICK_CNT, COALESCE(SUM(VALID_AR_AMT),0) AS VALID_CNT FROM NEO_AR_LINKLOG WHERE
NEO_AR_LINKLOG.TASK_NO = #{joinKey.taskNo}
) C

SELECT A.STEP1 , A.STEP2 , COALESCE(B.SEND_AR_AMT,0) AS CNT_STEP2 , A.CD_DESC AS NM_STEP2 FROM ( SELECT STEP1, STEP2, CD_DESC FROM NEO_RCODE WHERE KIND = '002' AND UILANG = #{uilang} ) A, ( SELECT RCODE_STEP1, RCODE_STEP2, SUM(SEND_AR_AMT) AS SEND_AR_AMT FROM NEO_AR_SENDLOG WHERE SEND_RCODE != '000' AND
NEO_AR_SENDLOG.TASK_NO = #{joinKey.taskNo}
GROUP BY RCODE_STEP1, RCODE_STEP2 ) B ON A.STEP1 = B.RCODE_STEP1 AND A.STEP2 = B.RCODE_STEP2 ORDER BY A.STEP1, A.STEP2

SELECT NEO_AR_SENDLOG.CAMP_NO , NEO_CAMPAIGN.CAMP_NM , COALESCE(SUM(SEND_AR_AMT),0) AS TOT_CNT , COALESCE(SUM((CASE WHEN NEO_AR_SENDLOG.SEND_RCODE = '000' THEN SEND_AR_AMT END)),0) AS SUC_CNT FROM NEO_AR_SENDLOG , NEO_CAMPAIGN WHERE NEO_AR_SENDLOG.CAMP_NO = NEO_CAMPAIGN.CAMP_NO AND
NEO_AR_SENDLOG.CAMP_NO = #{campNo}
GROUP BY NEO_AR_SENDLOG.CAMP_NO, NEO_CAMPAIGN.CAMP_NM ORDER BY NEO_AR_SENDLOG.CAMP_NO DESC

SELECT OPEN_CNT , BLOCK_CNT , VALID_CNT , CLICK_CNT FROM ( SELECT COALESCE(SUM((CASE WHEN BLOCKED_YN = 'N' THEN RESP_AR_AMT END)),0) AS OPEN_CNT, COALESCE(SUM((CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT END)),0) AS BLOCK_CNT FROM NEO_AR_RESPLOG WHERE
NEO_AR_RESPLOG.CAMP_NO = #{campNo}
) A, ( SELECT COALESCE(SUM(CLICK_AR_AMT),0) AS CLICK_CNT, COALESCE(SUM(VALID_AR_AMT),0) AS VALID_CNT FROM NEO_AR_LINKLOG WHERE
NEO_AR_LINKLOG.CAMP_NO = #{campNo}
) C

SELECT A.STEP1 , A.STEP2 , COALESCE(B.SEND_AR_AMT,0) AS CNT_STEP2 , A.CD_DESC AS NM_STEP2 FROM ( SELECT STEP1, STEP2, CD_DESC FROM NEO_RCODE WHERE KIND = '002' AND UILANG = #{uilang} ) A LEFT OUTER JOIN ( SELECT RCODE_STEP1, RCODE_STEP2, SUM(SEND_AR_AMT) AS SEND_AR_AMT FROM NEO_AR_SENDLOG WHERE SEND_RCODE != '000' AND
NEO_AR_SENDLOG.CAMP_NO = #{campNo}
GROUP BY RCODE_STEP1, RCODE_STEP2 ) B ON A.STEP1 = B.RCODE_STEP1 AND A.STEP2 = B.RCODE_STEP2 ORDER BY A.STEP1, A.STEP2

SELECT AA.YMD , SEND_CNT , COALESCE(SUCC_CNT,0) SUCC_CNT , COALESCE(OPEN_CNT,0) OPEN_CNT , COALESCE(VALID_CNT,0) VALID_CNT , COALESCE(CLICK_CNT,0) CLICK_CNT , COALESCE(BLOCK_CNT,0) BLOCK_CNT FROM ( SELECT TO_CHAR((TO_DATE(A.SEND_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMM') YMD, COALESCE(SUM(SEND_AR_AMT),0) SEND_CNT, COALESCE(SUM(CASE WHEN SEND_RCODE = '000' THEN SEND_AR_AMT ELSE 0 END),0) SUCC_CNT FROM NEO_AR_SENDLOG A, NEO_USER B, NEO_TIMEZONE C WHERE A.USER_ID = B.USER_ID AND B.TZ_CD = C.TZ_CD AND A.SEND_DT >= #{searchStartDt} || '0000' AND A.SEND_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY TO_CHAR((TO_DATE(A.SEND_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMM') ) AA LEFT OUTER JOIN ( SELECT TO_CHAR((TO_DATE(A.OPEN_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMM') YMD, COALESCE(SUM(RESP_AR_AMT),0) OPEN_CNT, COALESCE(SUM(CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT ELSE 0 END),0) BLOCK_CNT FROM NEO_AR_RESPLOG A, NEO_USER B, NEO_TIMEZONE C WHERE A.USER_ID = B.USER_ID AND B.TZ_CD = C.TZ_CD AND A.OPEN_DT >= #{searchStartDt} || '0000' AND A.OPEN_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY TO_CHAR((TO_DATE(A.OPEN_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMM') ) BB ON AA.YMD = BB.YMD LEFT OUTER JOIN ( SELECT TO_CHAR((TO_DATE(A.CLICK_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMM') YMD, COALESCE(SUM(CLICK_AR_AMT),0) CLICK_CNT, COALESCE(SUM(VALID_AR_AMT),0) VALID_CNT FROM NEO_AR_LINKLOG A, NEO_USER B, NEO_TIMEZONE C WHERE A.USER_ID = B.USER_ID AND B.TZ_CD = C.TZ_CD AND A.CLICK_DT >= #{searchStartDt} || '0000' AND A.CLICK_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY TO_CHAR((TO_DATE(A.CLICK_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMM') ) DD ON AA.YMD = DD.YMD ORDER BY AA.YMD

SELECT EE.WEEK_NM AS WEEK_NM , COALESCE(SEND_CNT,0) SEND_CNT , COALESCE(SUCC_CNT,0) SUCC_CNT , COALESCE(OPEN_CNT,0) OPEN_CNT , COALESCE(VALID_CNT,0) VALID_CNT , COALESCE(CLICK_CNT,0) CLICK_CNT , COALESCE(BLOCK_CNT,0) BLOCK_CNT FROM ( SELECT CD, CD_NM AS WEEK_NM FROM NEO_CD WHERE CD_GRP = 'C005' AND UILANG = #{uilang} ) EE, ( SELECT DAYOFWEEK, COALESCE(SUM(SEND_AR_AMT),0) SEND_CNT, COALESCE(SUM(CASE WHEN SEND_RCODE = '000' THEN SEND_AR_AMT ELSE 0 END),0) SUCC_CNT FROM NEO_AR_SENDLOG A WHERE A.SEND_DT >= #{searchStartDt} || '0000' AND A.SEND_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY DAYOFWEEK ) AA, ( SELECT DAYOFWEEK, COALESCE(SUM(RESP_AR_AMT),0) OPEN_CNT, COALESCE(SUM(CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT ELSE 0 END),0) BLOCK_CNT FROM NEO_AR_RESPLOG A WHERE A.OPEN_DT >= #{searchStartDt} || '0000' AND A.OPEN_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY DAYOFWEEK ) BB, ( SELECT DAYOFWEEK, COALESCE(SUM(CLICK_AR_AMT),0) CLICK_CNT, COALESCE(SUM(VALID_AR_AMT),0) VALID_CNT FROM NEO_AR_LINKLOG A WHERE A.CLICK_DT >= #{searchStartDt} || '0000' AND A.CLICK_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY DAYOFWEEK ) DD WHERE EE.CD = AA.DAYOFWEEK(+) AND EE.CD = BB.DAYOFWEEK(+) AND EE.CD = DD.DAYOFWEEK(+) ORDER BY EE.CD

SELECT AA.YMD , SEND_CNT , COALESCE(SUCC_CNT,0) SUCC_CNT , COALESCE(OPEN_CNT,0) OPEN_CNT , COALESCE(VALID_CNT,0) VALID_CNT , COALESCE(CLICK_CNT,0) CLICK_CNT , COALESCE(BLOCK_CNT,0) BLOCK_CNT FROM ( SELECT TO_CHAR((TO_DATE(A.SEND_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMMDD') YMD, COALESCE(SUM(SEND_AR_AMT),0) SEND_CNT, COALESCE(SUM(CASE WHEN SEND_RCODE = '000' THEN SEND_AR_AMT ELSE 0 END),0) SUCC_CNT FROM NEO_AR_SENDLOG A, NEO_USER B, NEO_TIMEZONE C WHERE A.USER_ID = B.USER_ID AND B.TZ_CD = C.TZ_CD AND A.SEND_DT >= #{searchStartDt} || '0000' AND A.SEND_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY TO_CHAR((TO_DATE(A.SEND_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMMDD') ) AA, ( SELECT TO_CHAR((TO_DATE(A.OPEN_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMMDD') YMD, COALESCE(SUM(RESP_AR_AMT),0) OPEN_CNT, COALESCE(SUM(CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT ELSE 0 END),0) BLOCK_CNT FROM NEO_AR_RESPLOG A, NEO_USER B, NEO_TIMEZONE C WHERE A.USER_ID = B.USER_ID AND B.TZ_CD = C.TZ_CD AND A.OPEN_DT >= #{searchStartDt} || '0000' AND A.OPEN_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY TO_CHAR((TO_DATE(A.OPEN_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMMDD') ) BB, ( SELECT TO_CHAR((TO_DATE(A.CLICK_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMMDD') YMD, COALESCE(SUM(CLICK_AR_AMT),0) CLICK_CNT, COALESCE(SUM(VALID_AR_AMT),0) VALID_CNT FROM NEO_AR_LINKLOG A, NEO_USER B, NEO_TIMEZONE C WHERE A.USER_ID = B.USER_ID AND B.TZ_CD = C.TZ_CD AND A.CLICK_DT >= #{searchStartDt} || '0000' AND A.CLICK_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY TO_CHAR((TO_DATE(A.CLICK_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMMDD') ) DD WHERE AA.YMD = BB.YMD(+) AND AA.YMD = DD.YMD(+) ORDER BY AA.YMD

SELECT DOMAIN_NM , SEND_CNT , SUCC_CNT , ERR_CNT FROM ( SELECT CUST_DOMAIN DOMAIN_NM , SUM(SEND_AR_AMT) SEND_CNT , SUM(CASE WHEN SEND_RCODE = '000' THEN SEND_AR_AMT ELSE 0 END) AS SUCC_CNT , SUM(CASE WHEN (SEND_RCODE != '000' AND RCODE_STEP1 = '002') THEN SEND_AR_AMT ELSE 0 END) AS ERR_CNT FROM NEO_AR_DOMAINLOG A, NEO_TASK B WHERE A.TASK_NO = B.TASK_NO AND A.SEND_DAY||'0000' >= #{searchStartDt} || '0000' AND A.SEND_DAY||'0000' < #{searchEndDt} || '2400'
AND B.CAMP_NO = #{searchCampNo}
AND B.DEPT_NO = #{searchDeptNo}
AND B.USER_ID = #{searchUserId}
GROUP BY CUST_DOMAIN ORDER BY SUM(SEND_AR_AMT) DESC ) WHERE ROWNUM <= 20

SELECT EE.DEPT_NM AS DEPT_NM , SEND_CNT, COALESCE(SUCC_CNT,0) SUCC_CNT , COALESCE(OPEN_CNT,0) OPEN_CNT , COALESCE(VALID_CNT,0) VALID_CNT , COALESCE(CLICK_CNT,0) CLICK_CNT , COALESCE(BLOCK_CNT,0) BLOCK_CNT FROM ( SELECT DEPT_NO, COALESCE(SUM(SEND_AR_AMT),0) SEND_CNT , COALESCE(SUM(CASE WHEN SEND_RCODE = '000' THEN SEND_AR_AMT ELSE 0 END),0) SUCC_CNT FROM NEO_AR_SENDLOG A WHERE A.SEND_DT >= #{searchStartDt} || '0000' AND A.SEND_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY DEPT_NO ) AA, ( SELECT DEPT_NO, COALESCE(SUM(RESP_AR_AMT),0) OPEN_CNT , COALESCE(SUM(CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT ELSE 0 END),0) BLOCK_CNT FROM NEO_AR_RESPLOG A WHERE A.OPEN_DT >= #{searchStartDt} || '0000' AND A.OPEN_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY DEPT_NO ) BB, ( SELECT DEPT_NO, COALESCE(SUM(CLICK_AR_AMT),0) CLICK_CNT, COALESCE(SUM(VALID_AR_AMT),0) VALID_CNT FROM NEO_AR_LINKLOG A WHERE A.CLICK_DT >= #{searchStartDt} || '0000' AND A.CLICK_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY DEPT_NO ) DD, NEO_DEPT EE WHERE AA.DEPT_NO = BB.DEPT_NO(+) AND AA.DEPT_NO = DD.DEPT_NO(+) AND AA.DEPT_NO = EE.DEPT_NO(+)

SELECT EE.USER_NM AS USER_NM , SEND_CNT , COALESCE(SUCC_CNT,0) SUCC_CNT , COALESCE(OPEN_CNT,0) OPEN_CNT , COALESCE(VALID_CNT,0) VALID_CNT , COALESCE(CLICK_CNT,0) CLICK_CNT , COALESCE(BLOCK_CNT,0) BLOCK_CNT FROM ( SELECT USER_ID, COALESCE(SUM(SEND_AR_AMT),0) SEND_CNT , COALESCE(SUM(CASE WHEN SEND_RCODE = '000' THEN SEND_AR_AMT ELSE 0 END),0) SUCC_CNT FROM NEO_AR_SENDLOG A WHERE A.SEND_DT >= #{searchStartDt} || '0000' AND A.SEND_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY USER_ID ) AA, ( SELECT USER_ID, COALESCE(SUM(RESP_AR_AMT),0) OPEN_CNT , COALESCE(SUM(CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT ELSE 0 END),0) BLOCK_CNT FROM NEO_AR_RESPLOG A WHERE A.OPEN_DT >= #{searchStartDt} || '0000' AND A.OPEN_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY USER_ID ) BB, ( SELECT USER_ID, COALESCE(SUM(CLICK_AR_AMT),0) CLICK_CNT, COALESCE(SUM(VALID_AR_AMT),0) VALID_CNT FROM NEO_AR_LINKLOG A WHERE A.CLICK_DT >= #{searchStartDt} || '0000' AND A.CLICK_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY USER_ID ) DD, NEO_USER EE WHERE AA.USER_ID = BB.USER_ID(+) AND AA.USER_ID = DD.USER_ID(+) AND AA.USER_ID = EE.USER_ID(+)

SELECT EE.CAMP_NM AS CAMP_NM , SEND_CNT , COALESCE(SUCC_CNT,0) SUCC_CNT , COALESCE(OPEN_CNT,0) OPEN_CNT , COALESCE(VALID_CNT,0) VALID_CNT , COALESCE(CLICK_CNT,0) CLICK_CNT , COALESCE(BLOCK_CNT,0) BLOCK_CNT FROM ( SELECT CAMP_NO, COALESCE(SUM(SEND_AR_AMT),0) SEND_CNT , COALESCE(SUM(CASE WHEN SEND_RCODE = '000' THEN SEND_AR_AMT ELSE 0 END),0) SUCC_CNT FROM NEO_AR_SENDLOG A WHERE A.SEND_DT >= #{searchStartDt} || '0000' AND A.SEND_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY CAMP_NO ) AA, ( SELECT CAMP_NO, COALESCE(SUM(RESP_AR_AMT),0) OPEN_CNT , COALESCE(SUM(CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT ELSE 0 END),0) BLOCK_CNT FROM NEO_AR_RESPLOG A WHERE A.OPEN_DT >= #{searchStartDt} || '0000' AND A.OPEN_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY CAMP_NO ) BB, ( SELECT CAMP_NO, COALESCE(SUM(CLICK_AR_AMT),0) CLICK_CNT, COALESCE(SUM(VALID_AR_AMT),0) VALID_CNT FROM NEO_AR_LINKLOG A WHERE A.CLICK_DT >= #{searchStartDt} || '0000' AND A.CLICK_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY CAMP_NO ) DD, NEO_CAMPAIGN EE WHERE AA.CAMP_NO = BB.CAMP_NO(+) AND AA.CAMP_NO = DD.CAMP_NO(+) AND AA.CAMP_NO = EE.CAMP_NO(+)

SELECT SEND_DT , MAIL_FROM_NM , MAIL_FROM_EM , DEPT_NO , DEPT_NM , USER_ID , USER_NM , SEND_REPEAT , SEND_REPEAT_NM , TASK_NO , SUB_TASK_NO , TASK_NM , MAIL_TITLE , CUST_ID , CAMP_NO , CAMP_NM , CAMP_TY , CAMP_TY_NM , TARGET_GRP_TY , CUST_EM , CUST_NM , SEND_RCODE , SEND_RCODE_NM , RCODE_STEP1 , RCODE_STEP2 , RCODE_STEP3 , SEND_MSG , SEND_AMT , DOMAIN_CD , DOMAIN_NM , CUST_DOMAIN , TOTAL_COUNT FROM ( SELECT T1.SEND_DT , T3.MAIL_FROM_NM , T3.MAIL_FROM_EM , T1.DEPT_NO , T4.DEPT_NM , T1.USER_ID , T5.USER_NM , T3.SEND_REPEAT , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C017' AND CD = T3.SEND_REPEAT) SEND_REPEAT_NM , T1.TASK_NO , T1.SUB_TASK_NO , T3.TASK_NM , T3.MAIL_TITLE , T1.CUST_ID , T1.CAMP_NO , T6.CAMP_NM , T6.CAMP_TY , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C004' AND CD = T6.CAMP_TY) CAMP_TY_NM , T1.TARGET_GRP_TY , T1.CUST_EM , T1.CUST_NM , T1.SEND_RCODE , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C035' AND CD = T1.SEND_RCODE) SEND_RCODE_NM , T1.RCODE_STEP1 , T1.RCODE_STEP2 , T1.RCODE_STEP3 , T1.SEND_MSG , T1.SEND_AMT , T1.DOMAIN_CD , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C003' AND CD = T1.DOMAIN_CD) DOMAIN_NM , T1.CUST_DOMAIN , ROW_NUMBER() OVER (ORDER BY T1.SEND_DT DESC, T1.CUST_ID, T1.CUST_NM) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_SENDLOG T1 , NEO_SUBTASK T2 , NEO_TASK T3 , NEO_DEPT T4 , NEO_USER T5 , NEO_CAMPAIGN T6 WHERE T1.TASK_NO = T2.TASK_NO AND T1.SUB_TASK_NO = T2.SUB_TASK_NO AND T1.TASK_NO = T3.TASK_NO AND T1.DEPT_NO = T4.DEPT_NO AND T1.USER_ID = T5.USER_ID AND T1.CAMP_NO = T6.CAMP_NO
AND T1.SEND_DT >= CONCAT(#{searchStartDt},'000000')
AND T1.SEND_DT < CONCAT(#{searchEndDt},'240000')
AND T2.WORK_STATUS NOT IN ('000', '001', '002', '003', '201', '202', '203')
AND T2.WORK_STATUS = #{searchWorkStatus}
AND T1.DEPT_NO = #{searchDeptNo}
AND T1.USER_ID = #{searchUserId}
AND T5.USER_NM LIKE CONCAT('%',#{searchUserNm},'%')
AND T3.SEND_REPEAT = #{searchSendRepeat}
AND T3.SEG_NO = #{searchSegNo}
) TT WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows} ORDER BY SEQ

SELECT COALESCE(CONTENTS , '' ) CONTENTS
  FROM TS_MAILQUEUE A
 WHERE MID = #{subTaskNo}
   AND TID = #{taskNo}
 LIMIT 1

SELECT SEND_DT , MAIL_FROM_NM , MAIL_FROM_EM , USER_ID , USER_NM , SEND_REPEAT , SEND_REPEAT_NM , TASK_NO , SUB_TASK_NO , TASK_NM , MAIL_TITLE , CUST_ID , CAMP_NO , CAMP_NM , CUST_EM , CUST_NM , SEND_RCODE , SEND_RCODE_NM , ATT_CNT , SERVICE_GB , CONT_FL_PATH , WEB_AGENT FROM ( SELECT T1.SEND_DT , T3.MAIL_FROM_NM , T3.MAIL_FROM_EM , T1.USER_ID , T5.USER_NM , T3.SEND_REPEAT , (SELECT CD_NM FROM NEO_CD WHERE UILANG=#{uilang} AND CD_GRP='C017' AND CD=T3.SEND_REPEAT) SEND_REPEAT_NM , T1.TASK_NO , T1.SUB_TASK_NO , T3.TASK_NM , T3.MAIL_TITLE , T1.CUST_ID , T1.CAMP_NO , T6.CAMP_NM , T1.CUST_EM , T1.CUST_NM , T1.SEND_RCODE , (SELECT CD_NM FROM NEO_CD WHERE UILANG=#{uilang} AND CD_GRP='C035' AND CD=T1.SEND_RCODE) SEND_RCODE_NM , T3.ATT_CNT , '10' SERVICE_GB , T3.CONT_FL_PATH , (SELECT COALESCE(SOURCE_URL,'') FROM NEO_WEBAGENT WHERE TASK_NO = T1.TASK_NO) WEB_AGENT FROM NEO_SENDLOG T1 , NEO_SUBTASK T2 , NEO_TASK T3 , NEO_DEPT T4 , NEO_USER T5 , NEO_CAMPAIGN T6 WHERE T1.TASK_NO = T2.TASK_NO AND T1.SUB_TASK_NO = T2.SUB_TASK_NO AND T1.TASK_NO = T3.TASK_NO AND T1.DEPT_NO = T4.DEPT_NO AND T1.USER_ID = T5.USER_ID AND T1.CAMP_NO = T6.CAMP_NO
AND T1.SEND_DT >=CONCAT(#{searchStartDt}, '000000')
AND T1.SEND_DT < CONCAT(#{searchEndDt},'240000')
AND T1.CAMP_NO = #{searchCampNo}
AND T1.CUST_ID LIKE CONCAT('%', #{searchCustId}, '%')
AND T1.CUST_NM LIKE CONCAT('%', #{searchCustNm}, '%')
AND T1.CUST_EM = #{searchCustEm}
AND T1.SEND_RCODE = '000'
AND T1.SEND_RCODE != '000'
AND T5.USER_NM LIKE CONCAT('%', #{searchUserNm},'%')
UNION ALL SELECT TO_CHAR(Y.SDATE,'YYYYMMDD24MI') SEND_DT , X.SNAME MAIL_FROM_NM , X.SMAIL MAIL_FROM_EM , X.SID USER_ID , X.SNAME USER_NM , '099' SEND_REPEAT , '실시간' SEND_REPEAT_NM , X.TID TASK_NO , X.MID SUB_TASK_NO , C.TNM TASK_NM , Y.SUBJECT MAIL_TITLE , X.RID CUST_ID , X.TID CAMP_NO , C.TNM CAMP_NM , X.RMAIL CUST_EM , X.RNAME CUST_NM , X.RCODE SEND_RCODE , (SELECT CD_DESC FROM TS_RCODE WHERE UILANG = #{uilang} AND X.RCODE = RCODE ) SEND_RCODE_NM , C.ATTCH_CNT , '20' SERVICE_GB , (SELECT COALESCE(SOURCE_URL,'') FROM TS_WEBAGENT WHERE TID = X.TID) WEB_AGENT , C.CONTENTS_PATH CONT_FL_PATH FROM TS_RESULTLOG X , TS_SERVICETYP C , TS_MAILQUEUE Y , (SELECT MID, MAX(SUBID) SUBID FROM TS_MAILQUEUE GROUP BY MID) D WHERE X.TID = C.TID AND X.MID = Y.MID AND X.MID = D.MID AND X.SUBID= Y.SUBID AND X.TID = Y.TID
AND TO_CHAR(Y.SDATE, 'YYYYMMDD') >= #{searchStartDt}
AND TO_CHAR(Y.SDATE, 'YYYYMMDD') <= #{searchEndDt}
AND X.TID = #{searchCampNo}
AND X.RID LIKE CONCAT('%', #{searchCustId}, '%')
AAND X.RNAME LIKE CONCAT('%', #{searchCustNm}, '%')
AND X.RMAIL = #{searchCustEm}
AND CAST(X.RCODE AS INTEGER) = 1
AND CAST(X.RCODE AS INTEGER) > 1
AND X.SNAME LIKE CONCAT('%', #{searchUserNm},'%')
) ORDER BY SEND_DT DESC

SELECT CAMP_NO , CAMP_NM , TASK_COUNT , TASK_NO , TASK_NM , SEND_REPEAT , SEND_REPEAT_NM , USER_NM , DEPT_NO , CAMP_REG_DT , TASK_REG_DT , SEND_DT , WORK_STATUS_NM , TOTAL_COUNT FROM ( SELECT MT1.CAMP_NO , MT1.CAMP_NM , MT1.TASK_COUNT , MT1.TASK_NO , MT1.TASK_NM , MT1.SEND_REPEAT , MT1.SEND_REPEAT_NM , MT1.USER_NM , MT1.DEPT_NO , MT1.CAMP_REG_DT , MT1.TASK_REG_DT , MT1.SEND_DT , MT1.WORK_STATUS_NM , ROW_NUMBER() OVER (ORDER BY MT1.SEND_DT DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM( SELECT T1.CAMP_NO , T4.CAMP_NM , T1.SUB_TASK_CNT AS TASK_COUNT , T1.TASK_NO , T2.TASK_NM , T2.SEND_REPEAT , (SELECT MAX(CD_NM) FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C017' AND CD = T2.SEND_REPEAT) SEND_REPEAT_NM , (SELECT MAX(USER_NM) FROM NEO_USER WHERE USER_ID = T2.USER_ID) USER_NM , T2.DEPT_NO , T4.REG_DT AS CAMP_REG_DT , T2.REG_DT AS TASK_REG_DT , T5.SEND_DT , (SELECT MAX(CD_NM) FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C101' AND CD = T5.WORK_STATUS) WORK_STATUS_NM FROM ( SELECT NT1.CAMP_NO , CAST(SUBSTR(MAX(CONCAT(NS1.SEND_DT, LPAD(CONCAT(NT1.TASK_NO), '19' , '0'))), 13) AS INTEGER) AS TASK_NO , CAST(SUBSTR(MAX(CONCAT(NS1.SEND_DT, LPAD(CONCAT(NT1.TASK_NO), '19' , '0'), LPAD(CONCAT(NS1.SUB_TASK_NO), '19' , '0') )), 33) AS INTEGER ) AS SUB_TASK_NO , COUNT(*) SUB_TASK_CNT FROM NEO_TASK NT1 , NEO_SUBTASK NS1 WHERE NT1.TASK_NO = NS1.TASK_NO AND NS1.WORK_STATUS NOT IN ('000', '001', '002', '201','202','203','204') AND NS1.SEND_DT >= CONCAT( #{searchStartDt}, '000000') AND NS1.SEND_DT < CONCAT(#{searchEndDt}, '240000') AND NS1.SEND_TEST_YN = 'N' AND NT1.STATUS != '002'
AND NT1.CAMP_NO = #{searchCampNo}
AND NT1.USER_ID LIKE CONCAT('%', #{searchUserId}, '%')
AND NT1.DEPT_NO = #{searchDeptNo}
GROUP BY NT1.CAMP_NO ) T1 INNER JOIN NEO_TASK T2 ON (T1.TASK_NO = T2.TASK_NO ) INNER JOIN NEO_CAMPAIGN T4 ON (T1.CAMP_NO = T4.CAMP_NO ) INNER JOIN NEO_SUBTASK T5 ON (T1.TASK_NO = T5.TASK_NO AND T1.SUB_TASK_NO = T5.SUB_TASK_NO) ) MT1 ) TT WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows} ORDER BY SEQ

SELECT TASK_NO , TASK_NM , SUB_TASK_NO , SEG_NO , SEG_NM , CAMP_NO , CAMP_NM , USER_ID , MAIL_FROM_NM , STATUS , STATUS_NM , WORK_STATUS , SEND_TERM_START_DT , SEND_TERM_END_DT , WORK_STATUS_NM , WORK_STATUS_DTL , SEND_DT , END_DT , SEND_REPEAT , SEND_REPEAT_NM , MAIL_CNT , RTY_NO , TASK_REG_DT , SEND_TERM_LOOP , SEND_TERM_LOOP_TY_NM , SEG_RETRY , TOTAL_COUNT FROM ( SELECT AA.ORD_SEQ , AA.TASK_NO , AA.TASK_NM , AA.SUB_TASK_NO , AA.SEG_NO , AA.SEG_NM , AA.CAMP_NO , AA.CAMP_NM , AA.USER_ID , AA.MAIL_FROM_NM , AA.STATUS , AA.STATUS_NM , AA.WORK_STATUS , AA.SEND_TERM_START_DT , AA.SEND_TERM_END_DT , AA.WORK_STATUS_NM , AA.WORK_STATUS_DTL , AA.SEND_DT , AA.END_DT , AA.SEND_REPEAT , AA.SEND_REPEAT_NM , AA.MAIL_CNT , AA.RTY_NO , AA.TASK_REG_DT , AA.SEND_TERM_LOOP , AA.SEND_TERM_LOOP_TY_NM , AA.SEG_RETRY , ROW_NUMBER() OVER (ORDER BY AA.ORD_SEQ DESC, AA.TASK_NO, AA.SUB_TASK_NO) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM ( SELECT T0.ORD_SEQ , T1.TASK_NO , T1.TASK_NM , T2.SUB_TASK_NO , T1.SEG_NO , T3.SEG_NM , T1.CAMP_NO , T4.CAMP_NM , T1.USER_ID , T1.MAIL_FROM_NM , T1.STATUS , (SELECT MAX(CD_NM) FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C023' AND CD = T1.STATUS) STATUS_NM , T2.WORK_STATUS , (SELECT MIN(SEND_DT) FROM NEO_SUBTASK WHERE TASK_NO = T1.TASK_NO AND WORK_STATUS NOT IN ('000','001','002','201','202','203', '204') AND SEND_TEST_YN = 'N') SEND_TERM_START_DT , T1.SEND_TERM_END_DT , T6.CD_NM WORK_STATUS_NM , T6.CD_DTL WORK_STATUS_DTL , T2.SEND_DT , T2.END_DT , T1.SEND_REPEAT , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C017' AND CD = T1.SEND_REPEAT) SEND_REPEAT_NM , (SELECT COUNT(1) FROM NEO_SUBTASK WHERE TASK_NO = T1.TASK_NO) MAIL_CNT , COALESCE(T2.RTY_TASK_NO, 0 ) RTY_NO , T1.REG_DT TASK_REG_DT , CASE WHEN ( T1.SEND_TERM_LOOP IS NULL OR T1.SEND_TERM_LOOP = '' ) THEN '0' ELSE T1.SEND_TERM_LOOP END SEND_TERM_LOOP , (SELECT CD_NM FROM NEO_CD WHERE UILANG = #{uilang} AND CD_GRP = 'C019' AND CD = T1.SEND_TERM_LOOP_TY) SEND_TERM_LOOP_TY_NM , (SELECT COUNT(*) FROM NEO_SEGMENT_RETRY WHERE SEG_NO = T1.SEG_NO) SEG_RETRY FROM ( SELECT NT1.CAMP_NO , NT1.TASK_NO AS TASK_NO , NS1.SUB_TASK_NO , NS2.ORD_SEQ
FROM NEO_TASK NT1 , NEO_SUBTASK NS1 , ( SELECT ST1.TASK_NO TASK_NO , MAX(SS1.SEND_DT) AS ORD_SEQ FROM NEO_TASK ST1 , NEO_SUBTASK SS1 WHERE ST1.TASK_NO = SS1.TASK_NO AND SS1.WORK_STATUS NOT IN ('000', '001', '002', '201','202','203','204') AND SS1.SEND_DT >= CONCAT(#{searchStartDt}, '000000') AND SS1.SEND_DT < CONCAT(#{searchEndDt}, '240000') AND SS1.SEND_TEST_YN = 'N' AND ST1.STATUS != '002'
AND ST1.CAMP_NO = #{searchCampNo}
AND ST1.DEPT_NO = #{searchDeptNo}
AND ST1.DEPT_NO = #{searchDeptNo}
AND ST1.TASK_NM LIKE CONCAT('%', #{searchTaskNm}, '%')
AND ST1.SEND_REPEAT = #{searchSendRepeat}
AND ST1.USER_ID = #{searchUserId}
GROUP BY ST1.TASK_NO ) NS2 WHERE NT1.TASK_NO = NS1.TASK_NO AND NT1.TASK_NO = NS2.TASK_NO AND NS1.WORK_STATUS NOT IN ('000', '001', '002', '201','202','203','204') AND NS1.SEND_DT >= CONCAT(#{searchStartDt}, '000000') AND NS1.SEND_DT < CONCAT(#{searchEndDt}, '240000') AND NS1.SEND_TEST_YN = 'N' AND NT1.STATUS != '002'
AND NT1.CAMP_NO = #{searchCampNo}
AND NT1.DEPT_NO = #{searchDeptNo}
AND NT1.DEPT_NO = #{searchDeptNo}
AND ST1.TASK_NM LIKE CONCAT('%', #{searchTaskNm}, '%')
AND NT1.SEND_REPEAT = #{searchSendRepeat}
AND NT1.USER_ID = #{searchUserId}
) T0, NEO_TASK T1 , NEO_SUBTASK T2 , NEO_SEGMENT T3 , NEO_CAMPAIGN T4 , NEO_USER T5 , NEO_CD T6 WHERE T0.TASK_NO = T1.TASK_NO AND T0.TASK_NO = T2.TASK_NO AND T0.SUB_TASK_NO = T2.SUB_TASK_NO AND T1.SEG_NO = T3.SEG_NO AND T1.CAMP_NO = T4.CAMP_NO AND T1.USER_ID = T5.USER_ID AND T2.WORK_STATUS = T6.CD AND T6.UILANG = #{uilang} AND T6.CD_GRP = 'C101'
AND T2.WORK_STATUS NOT IN ('000','001','002','201','202','203','204')
AND T2.WORK_STATUS = #{searchWorkStatus}
AND T2.WORK_STATUS NOT IN ('000','001','002','003','201','202','203','204')
) AA ) TT WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows} ORDER BY SEQ

SELECT SEND_DT , MAIL_FROM_NM , MAIL_FROM_EM , USER_ID , USER_NM , SEND_REPEAT , SEND_REPEAT_NM , TASK_NO , SUB_TASK_NO , TASK_NM , MAIL_TITLE , CUST_ID , CAMP_NO , CAMP_NM , CUST_EM , CUST_NM , SEND_RCODE , SEND_RCODE_NM , ATTCH_CNT , SERVICE_GB , CONT_FL_PATH , WEB_AGENT , WEB_AGENT_TYP , RESP_AMT , BIZKEY , RTY_NO , TOTAL_COUNT FROM ( SELECT TT0.SEND_DT , TT0.MAIL_FROM_NM , TT0.MAIL_FROM_EM , TT0.USER_ID , TT0.USER_NM , TT0.SEND_REPEAT , TT0.SEND_REPEAT_NM , TT0.TASK_NO , TT0.SUB_TASK_NO , TT0.TASK_NM , TT0.MAIL_TITLE , TT0.CUST_ID , TT0.CAMP_NO , TT0.CAMP_NM , TT0.CUST_EM , TT0.CUST_NM , TT0.SEND_RCODE , TT0.SEND_RCODE_NM , TT0.ATTCH_CNT , TT0.SERVICE_GB , TT0.CONT_FL_PATH , TT0.WEB_AGENT , TT0.WEB_AGENT_TYP , TT0.RESP_AMT , TT0.BIZKEY , TT0.RTY_NO , ROW_NUMBER() OVER (ORDER BY TT0.SEND_DT DESC, TT0.CUST_ID, TT0.CUST_NM) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM ( SELECT TO_CHAR(Y.SDATE, 'YYYYMMDDHH24MI') SEND_DT , X.SNAME MAIL_FROM_NM , X.SMAIL MAIL_FROM_EM , X.SID USER_ID , X.SNAME USER_NM , '099' SEND_REPEAT , '실시간' SEND_REPEAT_NM , X.TID TASK_NO , X.MID SUB_TASK_NO , C.TNM TASK_NM , Y.SUBJECT MAIL_TITLE , X.RID CUST_ID , X.TID CAMP_NO , C.TNM CAMP_NM , X.RMAIL CUST_EM , X.RNAME CUST_NM , X.RCODE SEND_RCODE , (SELECT CD_DESC FROM TS_RCODE WHERE UILANG = #{uilang} AND X.RCODE = RCODE ) SEND_RCODE_NM , C.ATTCH_CNT , '20' SERVICE_GB , C.CONTENTS_PATH CONT_FL_PATH , (SELECT COALESCE(SOURCE_URL, '') FROM TS_WEBAGENT WHERE TID = X.TID) WEB_AGENT , (SELECT COALESCE(SECU_ATT_TYP, '') FROM TS_WEBAGENT WHERE TID = X.TID) WEB_AGENT_TYP , COALESCE(L.RSID, 0 ) RESP_AMT , X.BIZKEY , COALESCE(Y.RTY_MID, 0 ) RTY_NO FROM TS_MAILQUEUE Y LEFT OUTER JOIN TS_SERVICETYP C ON (Y.TID = C.TID) INNER JOIN TS_RESULTLOG X ON (X.MID = Y.MID AND X.SUBID= Y.SUBID ) LEFT OUTER JOIN TS_WEBAGENT T ON ( X.TID = T.TID) LEFT OUTER JOIN TS_RESPONSELOG L ON ( X.MID = L.MID AND X.SUBID = L.SUBID AND X.RID = L.RID ) WHERE Y.TID = #{searchCampNo} AND NOT EXISTS (SELECT AA.MID FROM TS_MAILQUEUE_TEST AA WHERE AA.MID = Y.MID ) AND COALESCE(Y.STATUS , 1 ) > 0
AND Y.SDATE >= STR_TO_DATE(CONCAT(#{searchStartDt},'000000'),'%Y%m%d%H%i%s')
AND Y.SDATE <= STR_TO_DATE(CONCAT(#{searchEndDt},'235959'),'%Y%m%d%H%i%s')
AND X.RID LIKE CONCAT('%', #{searchCustId},'%')
AND X.RNAME LIKE CONCAT('%', #{searchCustNm}, '%')
AND X.RMAIL = #{searchCustEm}
AND X.RCODE = '1'
AND X.RCODE != '1'
AND X.SNAME LIKE CONCAT('%', #{searchUserNm}, '%')
) TT0 ) TT WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}
