SELECT * FROM ( SELECT MSGID , KEYGEN , USER_ID , DEPT_NO , DEPT_NM , CAMPUS_NO AS CAMP_NO , CAMP_NM , SEND_DATE , TASK_NM , TEMP_CD , TEMP_NM , VALID_YN , SMS_NAME , SMS_MESSAGE , GUBUN , GUBUN_NM , SEND_TOT_CNT , SUCC_CNT , FAIL_CNT , SUCC_SMS , FAIL_SMS , TOTAL_COUNT FROM ( SELECT 'X' JOIN_COL , A.MSGID , A.KEYGEN , A.USER_ID , A.DEPT_NO , B.DEPT_NM , A.CAMPUS_NO , (SELECT CAMP_NM FROM NEO_CAMPAIGN T1 WHERE T1.CAMP_NO = A.CAMPUS_NO ) AS CAMP_NM , A.SEND_DATE , A.TASK_NM , IF(A.TEMP_CD IS NULL OR A.TEMP_CD ='' , '999999', A.TEMP_CD ) TEMP_CD , IFNULL(E.TEMP_NM ,'웹발송' ) TEMP_NM , A.VALID_YN , A.SMS_NAME , A.SMS_MESSAGE , A.GUBUN , D.CD_NM AS GUBUN_NM , (SELECT COUNT(*) FROM NEO_SMSLOG T1 WHERE T1.MSGID = A.MSGID AND T1.KEYGEN = A.KEYGEN) SEND_TOT_CNT
, (SELECT COUNT(*) FROM NEO_SMSLOG T1 WHERE T1.MSGID = A.MSGID AND T1.KEYGEN = A.KEYGEN AND T1.RSLT_CD = '1' AND T1.SMS_SEND_YN = 'N' ) AS SUCC_CNT , (SELECT COUNT(*) FROM NEO_SMSLOG T1 WHERE T1.MSGID = A.MSGID AND T1.KEYGEN = A.KEYGEN AND T1.RSLT_CD = '0' AND T1.SMS_SEND_YN = 'N' ) AS FAIL_CNT , (SELECT COUNT(*) FROM NEO_SMSLOG T1 WHERE T1.MSGID = A.MSGID AND T1.KEYGEN = A.KEYGEN AND T1.RSLT_CD = '1' AND T1.SMS_SEND_YN = 'Y' ) AS SUCC_SMS , (SELECT COUNT(*) FROM NEO_SMSLOG T1 WHERE T1.MSGID = A.MSGID AND T1.KEYGEN = A.KEYGEN AND T1.RSLT_CD = '0' AND T1.SMS_SEND_YN = 'Y' ) AS FAIL_SMS
, (SELECT COUNT(*) FROM NEO_SMSLOG T1 WHERE T1.MSGID = A.MSGID AND T1.KEYGEN = A.KEYGEN AND T1.RSLT_CD = '1' ) AS SUCC_CNT , (SELECT COUNT(*) FROM NEO_SMSLOG T1 WHERE T1.MSGID = A.MSGID AND T1.KEYGEN = A.KEYGEN AND T1.RSLT_CD = '0' ) AS FAIL_CNT , 0 AS SUCC_SMS , 0 AS FAIL_SMS
FROM NEO_SMS A INNER JOIN NEO_DEPT B ON A.DEPT_NO = B.DEPT_NO INNER JOIN NEO_CD D ON D.CD_GRP = 'C115' AND A.GUBUN = D.CD
LEFT OUTER JOIN NEO_SMS_TEMPLATE E ON IF(A.TEMP_CD IS NULL OR A.TEMP_CD ='' , '999999', A.TEMP_CD ) = E.TEMP_CD
LEFT OUTER JOIN NEO_KAKAOTALK_TEMPLATE E ON IF(A.TEMP_CD IS NULL OR A.TEMP_CD ='' , '999999', A.TEMP_CD ) = E.TEMP_CD
WHERE A.SMS_STATUS = '000' AND A.STATUS IN ('003','004')
AND A.GUBUN != '004'
AND A.GUBUN = #{searchGubun}
AND A.SEND_DATE >= CONCAT(#{searchStartDt},'000000')
AND A.SEND_DATE < CONCAT(#{searchEndDt},'240000')
AND A.DEPT_NO = #{searchDeptNo}
AND A.TASK_NM LIKE CONCAT('%', #{searchTaskNm},'%')
AND IF(A.TEMP_CD IS NULL OR A.TEMP_CD ='' , '999999', A.TEMP_CD ) = #{searchTempCd}
AND A.CAMPUS_NO = #{searchCampNo}
AND A.USER_ID = #{searchUserId}
) TT1, ( SELECT 'X' JOIN_COL , COUNT(*) TOTAL_COUNT FROM NEO_SMS A INNER JOIN NEO_DEPT B ON A.DEPT_NO = B.DEPT_NO INNER JOIN NEO_CD D ON D.CD_GRP = 'C115' AND A.GUBUN = D.CD
LEFT OUTER JOIN NEO_SMS_TEMPLATE E ON IF(A.TEMP_CD IS NULL OR A.TEMP_CD ='' , '999999', A.TEMP_CD ) = E.TEMP_CD
LEFT OUTER JOIN NEO_KAKAOTALK_TEMPLATE E ON A.TEMP_CD = E.TEMP_CD
WHERE A.SMS_STATUS = '000' AND A.STATUS IN ('003','004')
AND A.GUBUN != '004'
AND A.GUBUN = #{searchGubun}
AND A.SEND_DATE >= CONCAT(#{searchStartDt},'000000')
AND A.SEND_DATE < CONCAT(#{searchEndDt},'240000')
AND A.DEPT_NO = #{searchDeptNo}
AND A.TASK_NM LIKE CONCAT('%', #{searchTaskNm},'%')
AND IF(A.TEMP_CD IS NULL OR A.TEMP_CD ='' , '999999', A.TEMP_CD ) = #{searchTempCd}
AND A.CAMPUS_NO = #{searchCampNo}
AND A.USER_ID = #{searchUserId}
) TT2 WHERE TT1.JOIN_COL = TT2.JOIN_COL ORDER BY TT1.SEND_DATE DESC ) TTT LIMIT #{startRow}, #{rows}

SELECT A.MSGID
	 , A.KEYGEN
	 , IFNULL(B.PHONE , '') PHONE
	 , B.RSLT_CD
	 , B.SMS_SEND_YN
  FROM NEO_SMS A
	   LEFT OUTER JOIN NEO_SMSLOG B ON B.MSGID = A.MSGID AND B.KEYGEN = A.KEYGEN
 WHERE A.STATUS IN ('003','004')

   AND A.GUBUN != '004'


   AND A.GUBUN = #{gubun}

   AND A.SMS_STATUS = '000'

   AND B.PHONE = #{phone}

   AND A.MSGID = #{msgid}
   AND A.KEYGEN = #{keygen}

   AND B.RSLT_CD = #{rsltCd}


   AND B.SMS_SEND_YN = #{smsSendYn}

