SELECT * FROM ( SELECT TEMP_CD , TEMP_NM , USER_ID , USER_NM , SEND_DATE , SMS_NAME , SMS_MESSAGE , GUBUN , STATUS , SMS_REG_DT , TEMP_REG_DT , SMS_CNT , TASK_NM , STATUS_NM , TOTAL_COUNT FROM ( SELECT 'X' JOIN_COL , IF( A.TEMP_CD IS NULL OR A.TEMP_CD ='' , '999999', A.TEMP_CD ) TEMP_CD , IFNULL(C.TEMP_NM, '웹발송') TEMP_NM , A.USER_ID , (SELECT USER_NM FROM NEO_USER WHERE USER_ID = A.USER_ID ) USER_NM , A.SEND_DATE , A.SMS_NAME , A.SMS_MESSAGE , A.GUBUN , A.STATUS , A.REG_DT SMS_REG_DT , C.REG_DT TEMP_REG_DT , B.SMS_CNT , A.TASK_NM , (SELECT CD_NM FROM NEO_CD WHERE CD_GRP = 'C118' AND CD= A.STATUS AND UILANG ='000') STATUS_NM FROM NEO_SMS A INNER JOIN ( SELECT T2.TEMP_CD, MAX(T1.KEYGEN) KEYGEN , T2.SMS_CNT FROM NEO_SMS T1 INNER JOIN ( SELECT IF(TEMP_CD IS NULL OR TEMP_CD ='' , '999999', TEMP_CD ) TEMP_CD, MAX(SEND_DATE) SEND_DATE , COUNT(1) SMS_CNT FROM NEO_SMS WHERE SMS_STATUS = '000' AND STATUS IN ('003' , '004')
AND SEND_DATE >= CONCAT(#{searchStartDt},'000000')
AND SEND_DATE < CONCAT(#{searchEndDt},'240000')
AND DEPT_NO = #{searchDeptNo}
AND IF(TEMP_CD IS NULL OR TEMP_CD ='' , '999999', TEMP_CD ) = #{searchTempCd}
AND USER_ID = #{searchUserId}
AND GUBUN = '004'
AND GUBUN != '004'
GROUP BY IF(TEMP_CD IS NULL OR TEMP_CD ='' , '999999', TEMP_CD ) ) T2 ON ( IF(T1.TEMP_CD IS NULL OR T1.TEMP_CD ='' , '999999', T1.TEMP_CD )= T2.TEMP_CD AND T1.SEND_DATE = T2.SEND_DATE ) WHERE SMS_STATUS = '000' AND STATUS IN ('003' , '004')
AND T1.SEND_DATE >= CONCAT(#{searchStartDt},'000000')
AND T1.SEND_DATE < CONCAT(#{searchEndDt},'240000')
AND T1.DEPT_NO = #{searchDeptNo}
AND IF(T1.TEMP_CD IS NULL OR T1.TEMP_CD ='' , '999999', T1.TEMP_CD ) = #{searchTempCd}
AND T1.USER_ID = #{searchUserId}
AND T1.GUBUN = '004'
AND T1.GUBUN != '004'
GROUP BY T2.TEMP_CD ,T2.SMS_CNT ) B ON IF(A.TEMP_CD IS NULL OR A.TEMP_CD ='' , '999999', A.TEMP_CD ) = B.TEMP_CD AND A.KEYGEN = B.KEYGEN
LEFT OUTER JOIN NEO_SMS_TEMPLATE C ON A.TEMP_CD = C.TEMP_CD
LEFT OUTER JOIN NEO_KAKAOTALK_TEMPLATE C ON A.TEMP_CD = C.TEMP_CD
) TT1 , ( SELECT 'X' JOIN_COL , COUNT(*) TOTAL_COUNT FROM NEO_SMS A INNER JOIN ( SELECT T2.TEMP_CD, MAX(T1.KEYGEN) KEYGEN , T2.SMS_CNT FROM NEO_SMS T1 INNER JOIN ( SELECT IF(TEMP_CD IS NULL OR TEMP_CD ='' , '999999', TEMP_CD ) TEMP_CD, MAX(SEND_DATE) SEND_DATE , COUNT(1) SMS_CNT FROM NEO_SMS WHERE SMS_STATUS = '000' AND STATUS IN ('003' , '004')
AND SEND_DATE >= CONCAT(#{searchStartDt},'000000')
AND SEND_DATE < CONCAT(#{searchEndDt},'240000')
AND DEPT_NO = #{searchDeptNo}
AND IF(TEMP_CD IS NULL OR TEMP_CD ='' , '999999', TEMP_CD ) = #{searchTempCd}
AND USER_ID = #{searchUserId}
AND GUBUN = '004'
AND GUBUN != '004'
GROUP BY IF(TEMP_CD IS NULL OR TEMP_CD ='' , '999999', TEMP_CD ) ) T2 ON ( IF(T1.TEMP_CD IS NULL OR T1.TEMP_CD ='' , '999999', T1.TEMP_CD )= T2.TEMP_CD AND T1.SEND_DATE = T2.SEND_DATE ) WHERE SMS_STATUS = '000' AND STATUS IN ('003' , '004')
AND T1.SEND_DATE >= CONCAT(#{searchStartDt},'000000')
AND T1.SEND_DATE < CONCAT(#{searchEndDt},'240000')
AND T1.DEPT_NO = #{searchDeptNo}
AND IF(T1.TEMP_CD IS NULL OR T1.TEMP_CD ='' , '999999', T1.TEMP_CD ) = #{searchTempCd}
AND T1.USER_ID = #{searchUserId}
AND T1.GUBUN = '004'
AND T1.GUBUN != '004'
GROUP BY T2.TEMP_CD ,T2.SMS_CNT ) B ON IF(A.TEMP_CD IS NULL OR A.TEMP_CD ='' , '999999', A.TEMP_CD ) = B.TEMP_CD AND A.KEYGEN = B.KEYGEN
LEFT OUTER JOIN NEO_SMS_TEMPLATE C ON A.TEMP_CD = C.TEMP_CD
LEFT OUTER JOIN NEO_KAKAOTALK_TEMPLATE C ON A.TEMP_CD = C.TEMP_CD
) TT2 ) TTT LIMIT #{startRow}, #{rows}

SELECT * FROM ( SELECT TT1.CAMP_NO , TT1.CAMP_NM /* 캠페인명 */ , TT1.SEND_DATE /* 최종발송일자 */ , TT1.SMS_CNT /* 건수 */ , TT1.TASK_NM /* 발송메세지명 */ , TT1.SEND_TYP /* 메세지유형 */ , TT1.SEND_TYP_NM /* 메일유형 */ , TT1.USER_ID /* 사용자명 */ , TT1.USER_NM /* 사용자명 */ , TT1.SMS_REG_DT /* 메세지등록일자 */ , TT1.CAMP_REG_DT /* 캠페인등록일자 */ , TT1.STATUS /* 발송상태 */ , TT1.STATUS_NM /* 발송상태 */ , TT2.TOTAL_COUNT FROM ( SELECT 'X' JOIN_COL , T1.CAMP_NO , T1.CAMP_NM /* 캠페인명 */ , T2.SEND_DATE /* 최종발송일자 */ , T3.SMS_CNT /* 건수 */ , T2.TASK_NM /* 발송메세지명 */ , T2.SEND_TYP /* 메세지유형 */ , T5.CD_NM AS SEND_TYP_NM /* 메일유형 */ , T2.USER_ID /* 사용자명 */ , T4.USER_NM /* 사용자명 */ , T2.REG_DT AS SMS_REG_DT /* 메세지등록일자 */ , T1.REG_DT AS CAMP_REG_DT /* 캠페인등록일자 */ , T2.STATUS /* 발송상태 */ , T6.CD_NM AS STATUS_NM /* 발송상태 */ FROM NEO_CAMPAIGN T1 INNER JOIN NEO_SMS T2 ON T1.CAMP_NO = T2.CAMPUS_NO INNER JOIN ( SELECT CAMPUS_NO AS CAMP_NO , MAX(CONCAT(SEND_DATE,NUM_VAL)) AS LAST_SEND_DATE , COUNT(1) AS SMS_CNT FROM NEO_SMS T01 INNER JOIN NEO_DEPT T02 ON T01.DEPT_NO = T02.DEPT_NO INNER JOIN NEO_CD T03 /* 전송 유형 */ ON T03.CD_GRP = 'C115' AND T01.GUBUN = T03.CD WHERE T01.SMS_STATUS = '000' AND T01.STATUS IN ('003','004')
AND T01.GUBUN != '004'
AND T01.GUBUN = #{searchGubun}
AND T01.SEND_DATE >= CONCAT(#{searchStartDt},'000000')
AND T01.SEND_DATE < CONCAT(#{searchEndDt},'240000')
AND T01.DEPT_NO = #{searchDeptNo}
AND T01.CAMPUS_NO = #{searchCampNo}
AND T01.USER_ID = #{searchUserId}
GROUP BY CAMPUS_NO ) T3 ON T1.CAMP_NO = T3.CAMP_NO AND CONCAT(T2.SEND_DATE, T2.NUM_VAL) = T3.LAST_SEND_DATE LEFT OUTER JOIN NEO_USER T4 ON T2.USER_ID = T4.USER_ID LEFT OUTER JOIN NEO_CD T5 /* 발송유형 */ ON T5.CD_GRP = 'C120' AND T2.SEND_TYP = T5.CD LEFT OUTER JOIN NEO_CD T6 /* 메세지상태 */ ON T6.CD_GRP = 'C101' AND T2.STATUS = T6.CD ) TT1, ( SELECT 'X' JOIN_COL , COUNT(*) TOTAL_COUNT FROM NEO_CAMPAIGN T1 INNER JOIN NEO_SMS T2 ON T1.CAMP_NO = T2.CAMPUS_NO INNER JOIN ( SELECT CAMPUS_NO AS CAMP_NO , MAX(CONCAT(SEND_DATE,NUM_VAL)) AS LAST_SEND_DATE , COUNT(1) AS SMS_CNT FROM NEO_SMS T01 INNER JOIN NEO_DEPT T02 ON T01.DEPT_NO = T02.DEPT_NO INNER JOIN NEO_CD T03 /* 전송 유형 */ ON T03.CD_GRP = 'C115' AND T01.GUBUN = T03.CD WHERE T01.SMS_STATUS = '000' AND T01.STATUS IN ('003','004')
AND T01.GUBUN != '004'
AND T01.GUBUN = #{searchGubun}
AND T01.SEND_DATE >= CONCAT(#{searchStartDt},'000000')
AND T01.SEND_DATE < CONCAT(#{searchEndDt},'240000')
AND T01.DEPT_NO = #{searchDeptNo}
AND T01.CAMPUS_NO = #{searchCampNo}
AND T01.USER_ID = #{searchUserId}
GROUP BY CAMPUS_NO ) T3 ON T1.CAMP_NO = T3.CAMP_NO AND CONCAT(T2.SEND_DATE, T2.NUM_VAL) = T3.LAST_SEND_DATE ) TT2 WHERE TT1.JOIN_COL = TT2.JOIN_COL ORDER BY TT1.SEND_DATE DESC ) TTT LIMIT #{startRow}, #{rows}
